import React, { useState, useEffect } from "react";
import { loginUser, registerUser, getProducts, addProduct } from "./api/api";
import { Button, Input, Card, CardContent } from "./components/ui";
import { useNavigate } from "react-router-dom";

export default function App() {
  const [page, setPage] = useState("home");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    if (page === "dashboard") {
      fetchProducts();
    }
  }, [page]);

  const handleLogin = async () => {
    try {
      const response = await loginUser(username, password);
      if (response.access) {
        localStorage.setItem("token", response.access);
        setPage("dashboard");
      }
    } catch (error) {
      alert("Invalid credentials. Please try again.");
    }
  };

  const handleRegister = async () => {
    try {
      await registerUser(username, password);
      alert("Registration successful! Please log in.");
      setPage("home");
    } catch (error) {
      alert("Registration failed. Try again with a strong password.");
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await getProducts();
      setProducts(response);
    } catch (error) {
      console.error("Failed to fetch products:", error);
    }
  };

  const handleAddProduct = async () => {
    try {
      await addProduct({ name: newProduct });
      fetchProducts();
      setNewProduct("");
    } catch (error) {
      console.error("Failed to add product:", error);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-6">
      {page === "home" && (
        <Card>
          <CardContent>
            <h2 className="text-xl font-bold mb-4">Login / Register</h2>
            <Input placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
            <Input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            <Button onClick={handleLogin} className="mt-4">Login</Button>
            <Button onClick={handleRegister} variant="secondary" className="mt-2">Register</Button>
          </CardContent>
        </Card>
      )}
      {page === "dashboard" && (
        <div>
          <h2 className="text-2xl font-bold mb-4">Product Dashboard</h2>
          <div className="flex gap-4">
            <Input placeholder="New Product" value={newProduct} onChange={(e) => setNewProduct(e.target.value)} />
            <Button onClick={handleAddProduct}>Add Product</Button>
          </div>
          <div className="mt-4">
            {products.map((product) => (
              <Card key={product.id} className="mb-2">
                <CardContent>{product.name}</CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}
