import axios from "axios";

const BASE_URL = "http://127.0.0.1:8000";
const API_URL = `${BASE_URL}/api`;  // Product catalog API
const USERS_URL = `${BASE_URL}/users`;  // User authentication API

// Get stored access token
const getToken = () => localStorage.getItem("accessToken");

// Create an Axios instance
const api = axios.create({
  baseURL: BASE_URL,
});

// Request Interceptor: Attach Authorization Header Automatically
api.interceptors.request.use(
  (config) => {
    const token = getToken();
    if (token) {
      config.headers["Authorization"] = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// Authentication APIs
export const loginUser = async (username, password) => {
  const response = await api.post(`${USERS_URL}/login/`, { username, password });
  localStorage.setItem("accessToken", response.data.access);
  localStorage.setItem("refreshToken", response.data.refresh);
  return response.data;
};

export const registerUser = async (username, password) => {
  return api.post(`${USERS_URL}/register/`, { username, password });
};

// Token Refresh
export const refreshAccessToken = async () => {
  const refreshToken = localStorage.getItem("refreshToken");
  if (!refreshToken) return null;

  try {
    const response = await api.post(`${USERS_URL}/token/refresh/`, { refresh: refreshToken });
    localStorage.setItem("accessToken", response.data.access);
    return response.data.access;
  } catch (error) {
    console.error("Failed to refresh token", error);
    localStorage.removeItem("accessToken");
    localStorage.removeItem("refreshToken");
    return null;
  }
};

// Product APIs (JWT-protected)
export const getProducts = () => api.get(`${API_URL}/products/`);
export const getProductById = (id) => api.get(`${API_URL}/products/${id}`);
export const addProduct = (product) => api.post(`${API_URL}/addProduct/`, product);
