import React, { useState, useEffect } from "react";
import { getProducts } from "../api/api"; // âœ… Ensure correct import path
import { useNavigate } from "react-router-dom";

export default function ProductList() {
  const [products, setProducts] = useState([]); // âœ… Default to empty array
  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await getProducts();
      setProducts(response || []);  // âœ… Ensure `response` is always an array
    } catch (error) {
      console.error("Error fetching products:", error);
      setProducts([]);  // âœ… Prevents undefined state
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("accessToken");
    navigate("/");
  };

  return (
    <div className="min-h-screen flex flex-col items-center p-6">
      <h2 className="text-2xl font-bold mb-4">Product Dashboard</h2>
      
      {/* ðŸ”´ Prevent `.map()` on undefined */}
      {products.length > 0 ? (
        <div className="mt-4">
          {products.map((product) => (
            <div key={product.id} className="p-4 border mb-2 rounded">
              {product.name}
            </div>
          ))}
        </div>
      ) : (
        <p className="text-gray-500">No products available.</p>
      )}

      {/* âœ… Logout Button */}
      <button 
        onClick={handleLogout} 
        className="bg-red-500 text-white px-4 py-2 mt-4 rounded">
        Logout
      </button>
    </div>
  );
}
